import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import {jwtDecode} from 'jwt-decode'; // Correction ici pour l'importation correcte de jwt-decode
import Denied from './Denied';

function PageAdmin() {
  const [role, setRole] = useState(''); // Utilisation de setRole pour définir le rôle
  const [userId, setUserId] = useState(''); // Utilisation de setUserId pour définir l'ID utilisateur
  const [token, setToken] = useState('');
  const [denied, setDenied] = useState(false);

  useEffect(() => {
    const value = Cookies.get('token');
    if (value) {
      try {
        const decodedToken = jwtDecode(value);
        setUserId(decodedToken.id);
        setRole(decodedToken.role); // Ajout de la définition du rôle
        setToken(value);
      } catch (error) {
        console.error('Invalid token', error);
        setDenied(true);
      }
    } else {
      setDenied(true);
    }
  }, []);

  return (
    <>
      {!denied && role === 'admin' ? (
        <div>admin</div>
      ) : (
        <Denied />
      )}
    </>
  );
}

export default PageAdmin;
