import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Login.css';
import Cookies from 'js-cookie';
import {jwtDecode} from 'jwt-decode'; // Importez jwtDecode depuis 'jwt-decode'

function PageUser() {
  const [token, setToken] = useState('');
  const [userId, setUserid] = useState('');
  const [fileBlob, setFileBlob] = useState(null);
  const [fileName, setFileName] = useState('file'); // Ajouter l'état pour le nom du fichier
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const value = Cookies.get('token');
      if (value) {
        const tokenData = JSON.parse(value);
        const decodedToken = jwtDecode(tokenData);
        setUserid(decodedToken.id);
        setToken(decodedToken.token);

        try {
          const response = await axios.post('http://localhost:8000/file/show', {
            token: decodedToken.token,
            userID: decodedToken.id
          }, {
            responseType: 'blob' // Définissez le type de réponse comme blob
          });

          // Vérifiez si la réponse est OK
          if (response.status === 200) {
            const contentDisposition = response.headers['content-disposition'];
            let fileName = 'file';
            if (contentDisposition && contentDisposition.indexOf('filename=') !== -1) {
              const matches = contentDisposition.match(/filename="([^"]+)"/);
              if (matches && matches[1]) {
                fileName = matches[1];
              }
            }
            setFileName(fileName);
            setFileBlob(response.data);
          }
        } catch (error) {
          console.error('Une erreur s\'est produite lors de la récupération du fichier :', error);
        }
      }
    };

    fetchData();
  }, []);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append('file', selectedFile);
    formData.append('nom', selectedFile.name);
    formData.append('taille', selectedFile.size);
    formData.append('userid', userId);

    try {
      const response = await axios.post('http://localhost:8000/file/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (response.status === 200) {
        console.log('Fichier téléversé avec succès');
        // Réinitialiser l'état du fichier sélectionné
        setSelectedFile(null);
        // Actualiser la liste des fichiers
        fetchData();
      }
    } catch (error) {
      console.error('Erreur lors du téléversement du fichier:', error);
    }
  };

  return (
    <div className="login-container">
      <label className='title'>Fichier téléchargé :</label>
      {fileBlob && (
        <a href={URL.createObjectURL(fileBlob)} download={fileName}>Télécharger le fichier</a>
      )}

      <div className="upload-container">
        <input type="file" onChange={handleFileChange} />
        <button onClick={handleUpload}>Téléverser</button>
      </div>
    </div>
  );
}

export default PageUser;
