import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import { jwtDecode } from 'jwt-decode';
import './Admin.css';
import { Bar, Line, Pie, Radar } from 'react-chartjs-2';
import 'chart.js/auto';

function PageAdmin() {
  const [role, setRole] = useState('');
  const [userId, setUserId] = useState('');
  const [token, setToken] = useState('');
  const [denied, setDenied] = useState(false);
  const [totalFile, setTotalFile] = useState(2);
  const [totalFileNow, setTotalFileNow] = useState(0);
  const [chartType, setChartType] = useState('bar');
  const [totalFileByUser, setFileByUser] = useState([]);
  const [AllFileOfUsers, setAllfilesOFUsers] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().slice(0, 10)); // Sélecteur de date

  useEffect(() => {
    const value = Cookies.get('token');
    if (value) {
      const decodedToken = jwtDecode(value);
      const tokenData = JSON.parse(value);

      setUserId(decodedToken.id);
      setRole(decodedToken.role);
      setToken(tokenData);
    }
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post('http://localhost:8000/file/fileAllcount', {
          token: token
        });
        if (response.status === 200) {
          setTotalFile(response.data);
        }
      } catch (error) {
        console.error('Une erreur s\'est produite lors de la récupération des fichiers :', error);
      }

      try {
        const responseNow = await axios.post('http://localhost:8000/file/fileAllcountNow', {
          token: token,
          date: selectedDate // Passez la date sélectionnée dans la requête
        });
        if (responseNow.status === 200) {
          setTotalFileNow(responseNow.data);
        }
      } catch (error) {
        console.error('Une erreur s\'est produite lors de la récupération des fichiers :', error);
      }

      try {
        const FileByUsers = await axios.post('http://localhost:8000/file/repartition', {
          token: token
        });
        if (FileByUsers.status === 200) {
          setFileByUser(FileByUsers.data);
          console.log(FileByUsers.data);
        }
      } catch (error) {
        console.error('Une erreur s\'est produite , attention :', error);
      }

      try {
        const AllFilesOFUsers = await axios.post('http://localhost:8000/file/showAllFiles', {
          token: token
        });
        if (AllFilesOFUsers.status === 200) {
          setAllfilesOFUsers(AllFilesOFUsers.data);
          console.log(AllFilesOFUsers.data);
        }
      } catch (error) {
        console.error('Une erreur s\'est produite , attention :', error);
      }

    };

    fetchData();
  }, [token, selectedDate]); // Ajoutez selectedDate comme dépendance

  const data = {
    labels: ['Total de fichier uploadé', 'Fichier uploadé aujourd\'hui'],
    datasets: [
      {
        label: 'Nombre de fichiers',
        data: [totalFile, totalFileNow],
        backgroundColor: 'rgba(75,192,192,0.6)',
        borderColor: 'rgba(75,192,192,1)',
        borderWidth: 1,
        
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        labels: {
          color: 'black', // Couleur blanche pour les labels de la légende
          font: {
            size: 18 // Taille de la police des labels
          }
        }
      }
    },
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  const renderChart = () => {
    switch (chartType) {
      case 'line':
        return <Line data={data} options={options} />;
      case 'pie':
        return <Pie data={data} options={options} />;
      case 'radar':
        return <Radar data={data} options={options} />;
      default:
        return <Bar data={data} options={options} />;
    }
  };

  return (
    <>
      {role === 'Admin' ? (
        <div>
          <div>
            <label htmlFor="chartType">Choisir le type de graphique : </label>
            <select id="chartType" value={chartType} onChange={(e) => setChartType(e.target.value)}>
              <option value="bar">Bar</option>
              <option value="line">Line</option>
              <option value="pie">Pie</option>
              <option value="radar">Radar</option>
            </select>
          </div>

          <div>
            <label htmlFor="dateSelect">Sélectionnez une date : </label>
            <input 
              type="date" 
              id="dateSelect" 
              value={selectedDate} 
              onChange={(e) => setSelectedDate(e.target.value)} 
            />
          </div>

          <div style={{ height: '50vh', width: '100vw' }}>
            {renderChart()}
          </div>

          <h2 className='title_table'>Liste des utilisateurs</h2>

          <div className='infos_users'>
            <table className='infosTable'>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Nom et prénom</th>
                  <th>Nombre de fichiers uploadés</th>
                  <th>Stockage restant (Go)</th>
                </tr>
              </thead>
              <tbody>
                {totalFileByUser.map(file => (
                  <tr key={file.userId}>
                    <td>{file.userId}</td>
                    <td>{file.nom} {file.prenom}</td>
                    <td>{file.nombre_de_fichiers}</td>
                    <td>{parseFloat(file.stockagedisponible / 1073741824).toFixed(3)+' Go utilisé sur les '+parseFloat(file.quantiteStockage / 1073741824).toFixed(3)+ ' Go'}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ) : (
        <div className="denied-container">
          <div className="overlay">
            <h1 className="denied-text">Vous n'avez pas le droit</h1>
          </div>
        </div>
      )}
    
      <table className='infosTable2'>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nom du fichier</th>
            <th>Taille</th>
            <th>Date de création</th>
            <th>Id de l'utilisateur</th>
          </tr>
        </thead>
        <tbody className='allfiles'>
          {AllFileOfUsers.map(file => (
            <tr key={file.id}>
              <td>{file.id}</td>
              <td>{file.nom}</td>
              <td>{parseFloat(file.Taille/ 1073741824).toFixed(5)+' Go '}</td>
              <td>{file.createdAt}</td>
              <td>{file.userid }</td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
}

export default PageAdmin;
