import React, { useState, useEffect } from 'react';
import { Outlet, Link } from "react-router-dom";

import Cookies from 'js-cookie';

const DeleteUser = () => {
    const [password, setPassword] = useState(false);
    const [email, setEmail] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
          const value = Cookies.get('token');
          const tokenData = JSON.parse(value);
          const decodedToken = jwtDecode(tokenData);
          setEmail(decodedToken.email);
          
        };
    
        fetchData();
      }, []);
    
    const handleDeleteUser = async () => {
        try {
          const data = {
            email: email,
            password:password
          };
    
          const response = await axios.post('http://localhost:8000/file/delete', data);
    
          if (response.status === 200) {
            alert('user supprimé avec succès');
          }
        } catch (error) {
          console.error('Une erreur s\'est produite lors de la suppression ', error);
        }
      };
    
    
      return (
        <>
          <p>Pour vous inscrire, vous devez payer un abonnement unique de 20€ et vous aurez le droit à 20Go de stockage</p>
          <div className="inscription-container">
            <label className="title">Inscription</label>
            {!isPaymentFormVisible ? (
              <form onSubmit={(e) => { e.preventDefault(); handleShowPaymentForm(); }}>
                <div className="form-group">
                  <label className="title2" htmlFor="email">Email</label>
                  <input
                    type="email"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label className="title2" htmlFor="nom">Nom</label>
                  <input
                    type="text"
                    id="nom"
                    value={nom}
                    onChange={(e) => setNom(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label className="title2" htmlFor="prenom">Prénom</label>
                  <input
                    type="text"
                    id="prenom"
                    value={prenom}
                    onChange={(e) => setPrenom(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label className="title2" htmlFor="adressePostal">Adresse postale</label>
                  <input
                    type="text"
                    id="adressePostal"
                    value={adressePostal}
                    onChange={(e) => setAdressePostal(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label className="title2" htmlFor="nomSociete">Nom de société</label>
                  <input
                    type="text"
                    id="nomSociete"
                    value={nomSociete}
                    onChange={(e) => setNomSociete(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label className="title2" htmlFor="adrSociete">Adresse postale de la société</label>
                  <input
                    type="text"
                    id="adrSociete"
                    value={adrSociete}
                    onChange={(e) => setAdrSociete(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label className="title2" htmlFor="siret">SIRET</label>
                  <input
                    type="text"
                    id="siret"
                    value={siret}
                    onChange={(e) => setSiret(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label className="title2" htmlFor="password">Mot de passe</label>
                  <input
                    type="password"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </div>
                {error && <div className="error-message">{error}</div>}
                <button type="submit">S'inscrire et payer</button>
              </form>
            ) : (
              <form onSubmit={handleSubmit}>
                <div className="form-group">
                  <img className='logo' src={logo} alt="Logo" />
                  <label className='label-card'>Nom du propriétaire:</label>
                  <input
                    type="text"
                    value={cardName}
                    onChange={handleCardNameChange}
                    maxLength="30"
                    placeholder="Nom sur la carte"
                    required
                  />
                </div>
                <div className="form-group">
                  <label className='label-card'>Numéro de carte bancaire:</label>
                  <input
                    type="text"
                    value={cardNumber}
                    onChange={handleCardNumberChange}
                    maxLength="19"
                    placeholder="XXXX XXXX XXXX XXXX"
                    required
                  />
                </div>
                <div className="form-group">
                  <label className='label-card'>Date d'expiration:</label>
                  <input
                    type="text"
                    value={cardDate}
                    onChange={handleCardDateChange}
                    placeholder="MM/AA"
                    required
                  />
                </div>
                <div className="form-group">
                  <label className='label-card'>Code secret:</label>
                  <input
                    type="text"
                    value={cardCode}
                    onChange={handleCardCodeChange}
                    maxLength="3"
                    placeholder="CVC"
                    required
                  />
                </div>
                <button type="submit">Payer</button>
              </form>
            )}
          </div>
        </>
      );
    }

export default DeleteUser;
