import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';
import Cookies from 'js-cookie';
import { jwtDecode } from 'jwt-decode'; // Importez jwtDecode depuis 'jwt-decode'




function LoginForm() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');




  
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
  
      console.log('Sending login request...');
      const response = await axios.post('http://localhost:8000/user/login', {
        email: email,
        password: password
      });


      if (response.status === 200) {
     
        sessionStorage.setItem('Role',JSON.stringify(response.data.role));
        
        Cookies.set('token', JSON.stringify(response.data), { expires: 7 }); // Le cookie expire dans 7 jours
        const value = Cookies.get('token');
 const tokenData = JSON.parse(value);
        
        // Extraire l'e-mail du token
        const decodedToken = jwtDecode(tokenData); // Utilisez jwtDecode
        const userEmail = decodedToken.id;
        alert(userEmail);
    


      } else {
        setError('Identifiants incorrects. Veuillez réessayer.');
      }
    } catch (error) {
      setError('Une erreur s\'est produite. Veuillez réessayer plus tard.');
      if (error.response) {
        console.error('Error response data:', error.response.data);
        console.error('Error response status:', error.response.status);
        console.error('Error response headers:', error.response.headers);
        setError(error.response.data.error || 'Une erreur s\'est produite. Veuillez réessayer plus tard.');
      } else if (error.request) {
        console.error('Error request:', error.request);
        setError('Aucune réponse du serveur. Veuillez vérifier votre connexion.');
      } else {
        console.error('Error message:', error.message);
        setError('Une erreur s\'est produite. Veuillez réessayer plus tard.');
      }
      console.error('Erreur lors de la connexion :', error);
    }
  };

  return (
    <>
      <div className="login-container">
        <label className='title'>Bienvenue</label>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className='title2' htmlFor="email">Email</label>
            <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
            />
          </div>
          <div className="form-group">
            <label className='title2' htmlFor="password">Mot de passe</label>
            <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
            />
          </div>
          {error && <div className="error-message">{error}</div>}
          <button type="submit">Se connecter</button>
        </form>
        <div className='image'>
    
        </div>
      </div>
      <div className="signup">
      <div className="signup-connect">
        <h1>Create your account</h1>
        <a href="#" className="btn btn-social btn-facebook"><i className="fa fa-facebook"></i> Sign in with Facebook</a>
        <a href="#" className="btn btn-social btn-twitter"><i className="fa fa-twitter"></i> Sign in with Twitter</a>
        <a href="#" className="btn btn-social btn-google"><i className="fa fa-google"></i> Sign in with Google</a>
        <a href="#" className="btn btn-social btn-linkedin"><i className="fa fa-linkedin"></i> Sign in with Linkedin</a>
      </div>
      <div className="signup-classic">
        <form className="form" onSubmit={handleSubmit}>
          <fieldset className="username">
          <label className='title2' htmlFor="email">Email</label>
            <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
            />
          </fieldset>
          <fieldset className="email">
            <input type="email" placeholder="email" />
          </fieldset>
          <fieldset className="password">
            <input type="password" placeholder="password" />
          </fieldset>
          <button type="submit" className="btn">sign up</button>
        </form>
      </div>
    </div>
      </>
  );
}

export default LoginForm;
