import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import {jwtDecode} from 'jwt-decode';
import './PageUser.css';
import logo from '../img/fichier.png';

function PageAccueil() {
  const [files, setFiles] = useState([]);
  const [userId, setUserid] = useState('');
  const [token, setToken] = useState('');
  const [fileName, setFileName] = useState('');
  const [fileToSend, setFileToSend] = useState(null);
  const [fileTaille, setFileTaille] = useState('');
  const [refreshFiles, setRefreshFiles] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      const value = Cookies.get('token');
      const tokenData = JSON.parse(value);
      const decodedToken = jwtDecode(tokenData);
      setUserid(decodedToken.id);
      setToken(decodedToken.token);

      try {
        const response = await axios.post('http://localhost:8000/file/show', {
          token: decodedToken.token,
          userID: decodedToken.id,
        });

        if (response.status === 200) {
          setFiles(response.data.files);
        }
      } catch (error) {
        console.error('Une erreur s\'est produite lors de la récupération des fichiers :', error);
      }
    };

    fetchData();
  }, [refreshFiles]);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
  
    if (file) {
      if (file.name.endsWith('.zip')) {
        setFileName(file.name);
        setFileToSend(file);
        setFileTaille(file.size);
      } else {
        alert('Veuillez sélectionner un fichier ZIP.');
      }
    }      
    console.log(file);
  };

  const handleFileUpload = async () => {
    try {
      const formData = new FormData();
      formData.append('file', fileToSend);
      formData.append('nom', fileName);
      formData.append('taille', fileTaille);
      formData.append('userid', userId);

      const response = await axios.post('http://localhost:8000/file/addFile', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.status === 200) {
        alert('Fichier envoyé avec succès');
        setRefreshFiles(!refreshFiles);
      }
    } catch (error) {
      console.error('Une erreur s\'est produite lors de l\'envoi du fichier :', error);
    }
  };

  const handleFileDelete = async (ID) => {
    try {
      const response = await axios.post('http://localhost:8000/file/delete', { id: ID });

      if (response.status === 200) {
        alert('Fichier supprimé avec succès');
        setRefreshFiles(!refreshFiles);
      }
    } catch (error) {
      console.error('Une erreur s\'est produite lors de la suppression du fichier :', error);
    }
  };

  return (
    <>
      <div className="upload-container">
        {Array.isArray(files) && files.map((file, index) => (
          <div className="file-container" key={index}>
            {file.file && (
              <a href={URL.createObjectURL(new Blob([new Uint8Array(file.file.data)], { type: file.type }))} download={file.nom}>
                <img className='logo' src={logo} alt="Logo" />
              </a>
            )}
            <p className='file-name'>{file.nom}</p>
            <button onClick={() => handleFileDelete(file.id)} className='file-name'>Supprimer</button>
          </div>
        ))}
      </div>
      <div>
        <input type="file" onChange={handleFileChange} />
        <div>
          <label>Nom du fichier :</label> {fileName}
        </div>
        <button onClick={handleFileUpload}>Envoyer</button>
      </div>
    </>
  );
}

export default PageAccueil;
