import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import {jwtDecode} from 'jwt-decode'; // Corrigé l'importation de jwtDecode
import './PageUser.css';
import logo from '../img/fichier.png';
import Delete from '../img/delete.png';

function PageAccueil() {
  const [role, setRole] = useState(null); // Utilisez null comme état initial
  const [userId, setUserId] = useState('');
  const [token, setToken] = useState('');
  const [loaded, setLoaded] = useState(false);

  useEffect(() => {
    const value = Cookies.get('token');
    if (value) {
      const tokenData = JSON.parse(value);
      const decodedToken = jwtDecode(tokenData);
      
      setUserId(decodedToken.id);
      setToken(tokenData);
      setRole(decodedToken.role);
    }
    setLoaded(true); // Indiquer que les informations ont été chargées
  }, []);

  useEffect(() => {
    console.log('Role state updated:', role);
  }, [role]);

  if (!loaded) {
    return <div>Loading...</div>;
  }

  if (role === '') {
    window.location.href = '/Denied';
    return null; // Retourner null pour ne pas rendre de contenu supplémentaire
  }

  return (
    <>
      Page admin
    </>
  );
}

export default PageAccueil;
