import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Login.css';
import Cookies from 'js-cookie';
import { jwtDecode } from 'jwt-decode'; // Importez jwtDecode depuis 'jwt-decode'



function PageAccueil() {
    const [token, setToken] = useState('');
    const [userId, setUserid] = useState('');

    const value = Cookies.get('token');
    const tokenData = JSON.parse(value);
    
    const decodedToken = jwtDecode(tokenData); 
    setUserid(decodedToken.id);
    setToken(decodedToken.token)

  useEffect(() => {
    reader.onloadend = async function () {
    // Effectuer une requête pour récupérer les événements
    const response = await axios.post('http://localhost:8000/file/show', {
        token: token,
        userID: userId
      });}
  }, []);
  if (response.status === 200) {
    alert('Inscription réussie');
    
    alert('ok')
  } 
  
  
  return (
      <div className="login-container">
        <label className='title'>Bienvenue</label>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className='title2' htmlFor="email">Email</label>
            <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
            />
          </div>
          <div className="form-group">
            <label className='title2' htmlFor="password">Mot de passe</label>
            <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
            />
          </div>
          {error && <div className="error-message">{error}</div>}
          <button type="submit">Se connecter</button>
        </form>
      </div>
  );
}

export default PageAccueil;
