import React, { useState } from 'react';
import axios from 'axios';
import './Inscription.css';
import jsPDF from 'jspdf';
import logo from '../img/logocard.png'; // Tell webpack this JS file uses this image

const generateTicketPDF = async (nom, prenom, adrpostale, nomSociete, adrSociete, siret, email, adressePostal, id) => {
  const doc = new jsPDF();
  const formattedDate = new Date().toLocaleDateString('fr-FR');
  const content = `
    Voici votre facture:
    Voici votre facture pour l'achat supplémentaire de 20Go de stockage.
    Vous avez donc 40Go de stockage !

    Nom:                                                  ${nom}
    Prénom:                                               ${prenom} 
    Adresse postale:                                      ${adressePostal}
    Nom de société:                                       ${nomSociete}
    Adresse postale de la société:                        ${adrSociete}
    SIRET:                                                ${siret} 
    Date de la facture:                                   ${formattedDate}
    Désignation:
    Quantité: 1
    Total hors taxe: 17,50 €
    Prix unitaire hors taxe:
    Montant de la TVA:
    Montant toutes taxes comprises à régler: 20€
  `;
  doc.text(content, 10, 10);

  // Convertir le PDF en blob
  const pdfBlob = doc.output('blob');

  try {
      const reader = new FileReader();
      reader.readAsDataURL(pdfBlob);
      reader.onloadend = async function () {
          try {
              // Envoyer le PDF par email
              await axios.post('http://localhost:8000/user/sendmail', {
                  to: email,
                  text: 'Voici votre facture',
                  fichier: reader.result.split(',')[1],
              });
              alert('E-mail envoyé avec succès.');

              // Envoyer le fichier PDF à l'API addFile
              const formData = new FormData();
              formData.append('file', pdfBlob, 'facture.pdf');
              formData.append('nom', 'Facture du supplément de stockage.pdf');
              formData.append('taille', pdfBlob.size);
              formData.append('userid', id);
              formData.append('type', 'pdf');

              const response = await axios.post('http://localhost:8000/file/addFile', formData, {
                  headers: {
                      'Content-Type': 'multipart/form-data',
                  },
              });

              console.log('File upload response:', response.data); // Log de la réponse de l'API
              window.location.href = '/';
          } catch (error) {
              console.error('Erreur lors de l\'envoi du fichier:', error);
              alert('Erreur lors de l\'envoi du fichier. Vérifiez la console pour plus de détails.');
          }
      };
  } catch (error) {
      console.error('Erreur lors de la génération du PDF:', error);
      alert('Erreur lors de la génération du PDF.');
  }
};




function Inscription() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [nom, setNom] = useState('');
  const [prenom, setPrenom] = useState('');
  const [adressePostal, setAdressePostal] = useState('');
  const [nomSociete, setNomSociete] = useState('');
  const [adrSociete, setAdrSociete] = useState('');
  const [siret, setSiret] = useState('');
  const [cardNumber, setCardNumber] = useState('');
  const [cardName, setCardName] = useState('');
  const [cardDate, setCardDate] = useState('');
  const [cardCode, setCardCode] = useState('');
  const [isPaymentFormVisible, setIsPaymentFormVisible] = useState(false);
  const [adminPassword, setAdminPassword] = useState('');
  const [id, setID] = useState(0);

  const handleShowPaymentForm = () => {
    setIsPaymentFormVisible(true);
  };

  const handleSubmit = async (e) => {
    
    e.preventDefault();
    setError('');

  // Vérifier le mot de passe Admin
  const role = adminPassword === '1234' ? 'Admin' : 'User';


  //ici on appelle l'api qui permet de créer l'utilisateur avec les informations qu'il à rentrée
    try {
      console.log('Sending registration request...');
      const response = await axios.post('http://localhost:8000/user/createUser', {
        nom: nom,
        prenom: prenom,
        email: email,
        adresse_postale: adressePostal,
        adresse_postale_societe: adrSociete,
        Nom_de_societe: nomSociete,
        siret: siret,
        password: password,
        quantiteStockage: 21474836480,
        stockagedisponible: 21474836480,
        role: role,
      });

