import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import {jwtDecode} from 'jwt-decode'; // Correction ici
import './PageUser.css';


function PageAdmin() {
  const [role, setRole] = useState(''); // Correction ici pour le nom de la fonction setter
  const [userId, setUserId] = useState(''); // Correction ici pour le nom de la fonction setter
  const [token, setToken] = useState('');
  const [denied, setDenied] = useState(false);

  useEffect(() => {
    const value = Cookies.get('token');
    if (value) {
      const decodedToken = jwtDecode(value);
      setUserId(decodedToken.id);
      setRole(decodedToken.role); // Ajout de la définition du rôle
      setToken(value);
    }
  }, []);

  return (
    <>
      {role !== '' ? (
        <div>admin</div>
      ) : (
        <div>user</div>
      )}
    </>
  );
}

export default PageAdmin;
