import React, { useState } from 'react';
import './App.css';
import axios from 'axios';

function LoginForm() {
  // Définir les états pour email, mot de passe et chargement
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  <Router>
  <Switch>
    <Route exact path="/" component={LoginForm} />
    <Route path="/dashboard" component={Accueil} />
    {/* Ajoutez d'autres routes au besoin */}
  </Switch>
</Router>
  // Fonction de gestion de la soumission du formulaire
  const handleSubmit = (event) => {
    event.preventDefault(); // Empêcher le comportement par défaut du formulaire
    setLoading(true); // Indiquer que la requête est en cours

    axios.post('http://127.0.0.1:8000/user/login', {
      email: email,
      password: password
    })
    .then((response) => {
      console.log(response.data);
      setLoading(false); 
      history.push('/dashboard'); // Remplacez '/dashboard' par le chemin de votre composant de tableau de bord

    })
    .catch((error) => {
      console.log(error);
      setLoading(false); // Réinitialiser l'état de chargement en cas d'erreur
    });
    
    setEmail('');
    setPassword('');
   
  };

  return (
    <form className="loginForm" onSubmit={handleSubmit}>
      <label htmlFor="email">Email:</label>
      <input
        type="text"
        id="email"
        name="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
      />
      <label htmlFor="password">Mot de passe:</label>
      <input
        type="password"
        id="password"
        name="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
      />
      <button type="submit" disabled={loading}>
        {loading ? 'Connexion en cours...' : 'Se connecter'}
      </button>
    </form>
  );
}

export default LoginForm;
