import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import { jwtDecode } from 'jwt-decode';

function PageAccueil() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [response, setResponse] = useState('');

  useEffect(() => {
    const value = Cookies.get('token');
    if (value) {
      const tokenData = jwtDecode(value);
      // Effectuez ici toute logique additionnelle si nécessaire avec le token décodé
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:8000/file/show', {
        email,
        password
      });

      if (response.status === 200) {
        alert('Connexion réussie');
        setResponse(response)
      }
    } catch (error) {
      setError('Erreur lors de la connexion. Veuillez réessayer.');
    }
  };

  return (
    <div className="login-container">
      <label className='title'>{response}</label>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label className='title2' htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label className='title2' htmlFor="password">Mot de passe</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {error && <div className="error-message">{error}</div>}
        <button type="submit">Se connecter</button>
      </form>
    </div>
  );
}

export default PageAccueil;
