import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Login.css';
import Cookies from 'js-cookie';
import { jwtDecode } from 'jwt-decode'; // Importez jwtDecode depuis 'jwt-decode'

function PageAccueil() {
  const [token, setToken] = useState('');
  const [userId, setUserid] = useState('');
  const [fileBlob, setFileBlob] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const value = Cookies.get('token');
      const tokenData = JSON.parse(value);
      const decodedToken = jwtDecode(tokenData);
      setUserid(decodedToken.id);
      setToken(decodedToken.token);
      
      try {
        const response = await axios.post('http://localhost:8000/file/show', {
          token: decodedToken.token,
          userID: decodedToken.id
        }, {
          responseType: 'blob' // Définissez le type de réponse comme blob
        });

        // Vérifiez si la réponse est OK
        if (response.status === 200) {
          const fileBlob = new Blob([response.data], { type: 'application/octet-stream' });
          setFileBlob(response.data.file);
        }
      } catch (error) {
        console.error('Une erreur s\'est produite lors de la récupération du fichier :', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="login-container">
      <label className='title'>Fichier blob récupéré :</label>
      {fileBlob && (
        <a href={URL.createObjectURL(fileBlob)} download="file.blob">Télécharger le fichier</a>
      )}
    </div>
  );
}

export default PageAccueil;
