import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import { jwtDecode } from 'jwt-decode';
import './PageUser.css';
import logo from '../img/fichier.png';
import Denied from './Denied';
import Delete from '../img/delete.png';

function PageAccueil() {
  const [files, setFiles] = useState([]); // Utilisez un tableau pour stocker les fichiers
  const [userId, setUserid] = useState('');
  const [token, setToken] = useState('');
  const [Denied, setDnied] = useState(false);

  // Données pour l'envoi
  const [fileName, setFileName] = useState('');
  const [fileToSend, setFileToSend] = useState();
  const [fileTaille, setFileTaille] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      const value = Cookies.get('token');
      const tokenData = JSON.parse(value);
      const decodedToken = jwtDecode(tokenData);
      setUserid(decodedToken.id);
      setToken(tokenData);
      try {
        const response = await axios.post('http://localhost:8000/file/show', {
          token: tokenData,
          userID: decodedToken.id,
        });

        if (response.status === 200) {
          // Stockez les fichiers dans l'état
          setFiles(response.data.files);
        }else{
            setDnied(true)
        }
      } catch (error) {
        console.error('Une erreur s\'est produite lors de la récupération du fichier :', error);
      }
    };

    fetchData();
  }, [files]);

  const handleFileChange = (event) => {
    const file = event.target.files[0]; // Obtient le premier fichier sélectionné
  
    if (file) {
      setFileName(file.name); // Met à jour le nom du fichier
      setFileToSend(file);
      setFileTaille(file.size); // Utilisez 'size' pour obtenir la taille du fichier

    }      
    console.log(file);

  };

  const handleFileUpload = async () => {
    try {
      const data = {
        nom: fileName,
        file: fileToSend,
        taille: fileTaille,
        userid: userId,
        token:token
      };
  
      const response = await axios.post('http://localhost:8000/file/addFile', data);
  
      if (response.status === 200) {
        // Mettez à jour l'état ou affichez un message de succès
        alert('Fichier envoyé avec succès');
      }
    } catch (error) {
      console.error('Une erreur s\'est produite lors de l\'envoi du fichier :', error);
    }
  };
  


  const handleFileDelete = async (ID) => {
    try {
      const data = {
        id: ID,
        token:token
      };

      const response = await axios.post('http://localhost:8000/file/delete', data);

      if (response.status === 200) {
        alert('Fichier supprimé avec succès');
        // mise à jours de la liste
        setFiles(files.filter(file => file.id !== ID));
      }
    } catch (error) {
      console.error('Une erreur s\'est produite lors de la suppression du fichier :', error);
    }
  };
  return (
    <>
   <a><img className='delete' src={Delete} alt="Delete" /></a> 
      <div className="upload-container">
        {Array.isArray(files) && files.map((file, index) => (
          <div className="file-container" key={index}>
            {file.file && (
              <a href={URL.createObjectURL(new Blob([new Uint8Array(file.file.data)], { type: 'application/pdf' }))} download={file.nom}>
                <img className='logo' src={logo} alt="Logo" />
              </a>
            )}
            <p className='file-name'>{file.nom}</p>
            <button onClick={() => handleFileDelete(file.id)} className='file-name'>Supprimer</button>
          </div>
        ))}
      </div>
      <div>
        <input type="file" onChange={handleFileChange} />
        <div>
          <label>Nom du fichier :</label> {fileName}
        </div>
        <button onClick={handleFileUpload}>Envoyer</button>
      </div>
    </>
  );
}

export default PageAccueil;
