import React, { useState } from 'react';
import axios from 'axios';
import './Inscription.css';
import jsPDF from 'jspdf';
import logo from '../img/logocard.png'; // Tell webpack this JS file uses this image

// Function to generate a PDF and send it to the new user
const generateTicketPDF = async ({ nom, prenom, adressePostal, nomSociete, adrSociete, siret, email }) => {
  const doc = new jsPDF();
  const formattedDate = new Date().toLocaleDateString('fr-FR');
  const content = `
    Voici votre facture:

    Nom: ${nom}
    Prénom: ${prenom} 
    Adresse postale: ${adressePostal}
    Nom de société: ${nomSociete}
    Adresse postale de la société: ${adrSociete}
    SIRET: ${siret} 
    Date de la facture: ${formattedDate}
    Désignation:
    Quantité: 1
    Total hors taxe: 17,50 €
    Prix unitaire hors taxe:
    Montant de la TVA:
    Montant toutes taxes comprises à régler: 20€
  `;
  doc.text(content, 10, 10);

  // Convert the PDF to a blob
  const pdfBlob = doc.output('blob');

  // Read the blob content as base64
  const reader = new FileReader();
  reader.readAsDataURL(pdfBlob);
  reader.onloadend = async function () {
    try {
      const response = await axios.post('http://localhost:8000/user/senmail', {
        to: email,
        text: 'Voici votre facture',
        fichier: reader.result.split(',')[1], // Extract the base64 content
      });

      const stockageFinal = 21474836480;

      // Send the PDF file
      const formData = new FormData();
      formData.append('file', pdfBlob, 'facture.pdf');
      formData.append('nom', 'Facture du supplément de stockage.pdf');
      formData.append('taille', pdfBlob.size);
      formData.append('userid', 35);
      formData.append('stockagedisponible', stockageFinal); // Check this value
      formData.append('type', 'pdf');

      await axios.post('http://localhost:8000/file/addFile', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      alert('E-mail envoyé : ' + response.data);
    } catch (error) {
      console.error('Erreur lors de l\'envoi de l\'e-mail :', error);
      alert('Erreur lors de l\'envoi de l\'e-mail : ' + error.message);
    }
  };
};

function Inscription() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [nom, setNom] = useState('');
  const [prenom, setPrenom] = useState('');
  const [adressePostal, setAdressePostal] = useState('');
  const [nomSociete, setNomSociete] = useState('');
  const [adrSociete, setAdrSociete] = useState('');
  const [siret, setSiret] = useState('');
  const [cardNumber, setCardNumber] = useState('');
  const [cardName, setCardName] = useState('');
  const [cardDate, setCardDate] = useState('');
  const [cardCode, setCardCode] = useState('');
  const [isPaymentFormVisible, setIsPaymentFormVisible] = useState(false);
  const [adminPassword, setAdminPassword] = useState('');

  const handleShowPaymentForm = () => {
    setIsPaymentFormVisible(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Check admin password
    const role = adminPassword === '1234' ? 'Admin' : 'User';

    // Call the API to create the user with the entered information
    try {
      console.log('Sending registration request...');
      const response = await axios.post('http://localhost:8000/user/createUser', {
        nom,
        prenom,
        email,
        adresse_postale: adressePostal,
        adresse_postale_societe: adrSociete,
        Nom_de_societe: nomSociete,
        siret,
        password,
        quantiteStockage: 21474836480,
        stockagedisponible: 21474836480,
        role,
      });

      console.log('Registration response:', response);

      if (response.status === 200) {
        alert('Inscription réussie');
        await generateTicketPDF({ nom, prenom, adressePostal, nomSociete, adrSociete, siret, email });
        window.location.href = '/';
      }
    } catch (error) {
      setError('Une erreur s\'est produite. Veuillez réessayer plus tard.');
      if (error.response) {
        console.error('Error response data:', error.response.data);
        setError(error.response.data.error || 'Une erreur s\'est produite. Veuillez réessayer plus tard.');
      } else if (error.request) {
        console.error('Error request:', error.request);
        setError('Aucune réponse du serveur. Veuillez vérifier votre connexion.');
      } else {
        console.error('Error message:', error.message);
        setError('Une erreur s\'est produite. Veuillez réessayer plus tard.');
      }
      console.error('Erreur lors de la connexion :', error);
    }
  };

  return (
    <div className='ensemble'>
      <div className='corpsgauche'></div>
      <div className='corpsdroite'>
        <div className='inscription'>
          {!isPaymentFormVisible ? (
            <form onSubmit={(e) => { e.preventDefault(); handleShowPaymentForm(); }}>
              <label className='title2'>Pour vous inscrire, vous devez payer un abonnement unique de 20€ et vous aurez le droit à 20Go de stockage</label>
              <div className="form-row">
                <div className="form-group">
                  <label className="title2" htmlFor="email">Email</label>
                  <input
                    type="email"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label className="title2" htmlFor="nom">Nom</label>
                  <input
                    type="text"
                    id="nom"
                    value={nom}
                    onChange={(e) => setNom(e.target.value)}
                    required
                  />
                </div>
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label className="title2" htmlFor="prenom">Prénom</label>
                  <input
                    type="text"
                    id="prenom"
                    value={prenom}
                    onChange={(e) => setPrenom(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label className="title2" htmlFor="adressePostal">Adresse postale</label>
                  <input
                    type="text"
                    id="adressePostal"
                    value={adressePostal}
                    onChange={(e) => setAdressePostal(e.target.value)}
                    required
                  />
                </div>
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label className="title2" htmlFor="nomSociete">Nom de société</label>
                  <input
                    type="text"
                    id="nomSociete"
                    value={nomSociete}
                    onChange={(e) => setNomSociete(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label className="title2" htmlFor="adrSociete">Adresse postale de la société</label>
                  <input
                    type="text"
                    id="adrSociete"
                    value={adrSociete}
                    onChange={(e) => setAdrSociete(e.target.value)}
                    required
                  />
                </div>
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label className="title2" htmlFor="siret">SIRET</label>
                  <input
                    type="text"
                    id="siret"
                    value={siret}
                    onChange={(e) => setSiret(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label className="title2" htmlFor="password">Mot de passe</label>
                  <input
                    type="password"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label className="title2" htmlFor="adminPassword">Mot de passe admin</label>
                  <input
                    type="password"
                    id="adminPassword"
                    value={adminPassword}
                    onChange={(e) => setAdminPassword(e.target.value)}
                    required
                  />
                </div>
              </div>
              <button type="submit">Continuer</button>
              {error && <div className="error">{error}</div>}
            </form>
          ) : (
            <form onSubmit={handleSubmit}>
              <h2>Informations de paiement</h2>
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="cardNumber">Numéro de carte</label>
                  <input
                    type="text"
                    id="cardNumber"
                    value={cardNumber}
                    onChange={(e) => setCardNumber(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="cardName">Nom sur la carte</label>
                  <input
                    type="text"
                    id="cardName"
                    value={cardName}
                    onChange={(e) => setCardName(e.target.value)}
                    required
                  />
                </div>
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="cardDate">Date d'expiration</label>
                  <input
                    type="text"
                    id="cardDate"
                    value={cardDate}
                    onChange={(e) => setCardDate(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="cardCode">Code de sécurité</label>
                  <input
                    type="text"
                    id="cardCode"
                    value={cardCode}
                    onChange={(e) => setCardCode(e.target.value)}
                    required
                  />
                </div>
              </div>
              <button type="submit">Valider le paiement</button>
              {error && <div className="error">{error}</div>}
            </form>
          )}
        </div>
      </div>
    </div>
  );
}

export default Inscription;
