import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Login.css';
import Cookies from 'js-cookie';
import {jwtDecode} from 'jwt-decode'; // Importation correcte de jwtDecode

function PageAccueil() {
  const [token, setToken] = useState('');
  const [userId, setUserid] = useState('');
  const [fileBlob, setFileBlob] = useState(null);
  const [fileName, setFileName] = useState('file'); // Ajouter l'état pour le nom du fichier

  useEffect(() => {
    const fetchData = async () => {
      const value = Cookies.get('token');
      if (value) {
        const tokenData = JSON.parse(value);
        const decodedToken = jwtDecode(tokenData.token);
        setUserid(decodedToken.id);
        setToken(decodedToken.token);
        
        try {
          const response = await axios.post('http://localhost:8000/file/show', {
            token: decodedToken.token,
            userID: decodedToken.id
          }, {
            responseType: 'arraybuffer' // Définissez le type de réponse comme arraybuffer
          });

          // Vérifiez si la réponse est OK
          if (response.status === 200) {
            const blob = new Blob([response.data], { type: response.headers['content-type'] });
            setFileBlob(blob);
            const disposition = response.headers['content-disposition'];
            if (disposition && disposition.includes('filename=')) {
              const filename = disposition.split('filename=')[1].split(';')[0].trim();
              setFileName(filename);
            }
          }
        } catch (error) {
          console.error('Une erreur s\'est produite lors de la récupération du fichier :', error);
        }
      }
    };

    fetchData();
  }, []);

  return (
    <div className="login-container">
      <label className='title'>Fichier téléchargé :</label>
      {fileBlob && (
        <a href={URL.createObjectURL(fileBlob)} download={fileName}>Télécharger le fichier</a>
      )}
    </div>
  );
}

export default PageAccueil;
