import React, { useState } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import { jwtDecode } from 'jwt-decode';
import Modal from './Modal'; // Importez le composant Modal
import './Login.css';

function LoginForm() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [modalMessage, setModalMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsModalVisible(false); // Assurez-vous que le modal est caché avant la soumission

    try {
      console.log('Sending login request...');
      const response = await axios.post('http://localhost:8000/user/login', {
        email: email,
        password: password
      });

      if (response.status === 200) {
        sessionStorage.setItem('Role', JSON.stringify(response.data.role));
        Cookies.set('token', JSON.stringify(response.data), { expires: 7 }); // Le cookie expire dans 7 jours
        const value = Cookies.get('token');
        const tokenData = JSON.parse(value);
        
        // Extraire l'e-mail du token
        const decodedToken = jwtDecode(tokenData.token); // Assurez-vous d'utiliser la clé correcte
        const userEmail = decodedToken.email; // Mettez à jour selon le nom du champ dans le token

        setModalMessage(userEmail); // Définir le message du modal
        setIsModalVisible(true); // Afficher le modal

      } else {
        setError('Identifiants incorrects. Veuillez réessayer.');
      }
    } catch (error) {
      setError('Une erreur s\'est produite. Veuillez réessayer plus tard.');
      if (error.response) {
        console.error('Error response data:', error.response.data);
        setError(error.response.data.error || 'Une erreur s\'est produite. Veuillez réessayer plus tard.');
      } else if (error.request) {
        console.error('Error request:', error.request);
        setError('Aucune réponse du serveur. Veuillez vérifier votre connexion.');
      } else {
        console.error('Error message:', error.message);
        setError('Une erreur s\'est produite. Veuillez réessayer plus tard.');
      }
      console.error('Erreur lors de la connexion :', error);
    }
  };

  const closeModal = () => {
    setIsModalVisible(false);
  };

  return (
    <>
      <div className='ensemble'>
        <div className='corpsgauche'></div>
        <div className='corpsdroite'>
          <div className="login">
            <form onSubmit={handleSubmit}>
              <h1 className='H1'>Veuillez vous connecter</h1>
              <div className="form-group">
                <label className='titre' htmlFor="email">Email</label>
                <input
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  placeholder='Username'
                />
              </div>
              <div className="form-group">
                <label className='titre' htmlFor="password">Mot de passe</label>
                <input
                  type="password"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  placeholder='Password'
                />
              </div>
              {error && <div className="error-message">{error}</div>}
              <button type="submit">Se connecter</button>
            </form>
          </div>
        </div>
      </div>

      {/* Afficher le modal si visiblement */}
      <Modal
        isVisible={isModalVisible}
        onClose={closeModal}
        message={`Vous êtes connecté avec l'email: ${modalMessage}`}
      />
    </>
  );
}

export default LoginForm;
