import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';
import Cookies from 'js-cookie';
import jwt_decode from 'jwt-decode'; // Utilisez jwt_decode avec une minuscule

function LoginForm() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      console.log('Sending login request...');
      const response = await axios.post('http://localhost:8000/user/login', {
        email: email,
        password: password
      });

      if (response.status === 200) {
        sessionStorage.setItem('Role', JSON.stringify(response.data.role));
        
        Cookies.set('test', JSON.stringify(response.data), { expires: 7 }); // Le cookie expire dans 7 jours

        // Récupérer le cookie et le parser
        const cookieValue = Cookies.get('test');
        const tokenData = JSON.parse(cookieValue);
        
        // Extraire l'e-mail du token
        const decodedToken = jwtDecode(tokenData.token); // Utilisez jwtDecode
        const userEmail = decodedToken.email;

        // Afficher l'e-mail dans une alerte
        alert(userEmail);
      } else {
        setError('Identifiants incorrects. Veuillez réessayer.');
      }
    } catch (error) {
      // Gestion des erreurs
    }
  };

  return (
      <div className="login-container">
        <label className='title'>Bienvenue</label>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className='title2' htmlFor="email">Email</label>
            <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
            />
          </div>
          <div className="form-group">
            <label className='title2' htmlFor="password">Mot de passe</label>
            <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
            />
          </div>
          {error && <div className="error-message">{error}</div>}
          <button type="submit">Se connecter</button>
        </form>
      </div>
  );
}

export default LoginForm;
