import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import {jwtDecode} from 'jwt-decode'; // Correction de l'import
import './Admin.css';
import { Bar, Line, Pie, Radar } from 'react-chartjs-2';
import 'chart.js/auto';

function PageAdmin() {
  const [role, setRole] = useState('');
  const [userId, setUserId] = useState('');
  const [token, setToken] = useState('');
  const [totalFile, setTotalFile] = useState(0);
  const [totalFileByDate, setTotalFileByDate] = useState(0);
  const [chartType, setChartType] = useState('bar');
  const [totalFileByUser, setFileByUser] = useState([]);
  const [allFilesOfUsers, setAllFilesOfUsers] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]); // Date du jour par défaut
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Récupération et décodage du token à l'initialisation
  useEffect(() => {
    const tokenFromCookie = Cookies.get('token');
    if (tokenFromCookie) {
      try {
        const decodedToken = jwtDecode(tokenFromCookie);
        setUserId(decodedToken.id);
        setRole(decodedToken.role);
        setToken(tokenFromCookie);
      } catch (err) {
        console.error('Erreur lors du décodage du token:', err);
        setError('Token invalide. Veuillez vous reconnecter.');
      }
    } else {
      setError('Aucun token trouvé. Veuillez vous connecter.');
    }
  }, []);

  // Fonction pour formater la date au format YYYY-MM-DD
  const formatDate = (date) => {
    const d = new Date(date);
    const month = `${(d.getMonth() + 1)}`.padStart(2, '0');
    const day = `${d.getDate()}`.padStart(2, '0');
    const year = d.getFullYear();
    return [year, month, day].join('-');
  };

  // Récupération des données à chaque changement de date ou lors de la récupération du token
  useEffect(() => {
    if (!token) return;

    const fetchData = async () => {
      setLoading(true);
      setError(null);

      try {
        // Récupérer le nombre total de fichiers
        const totalFilesResponse = await axios.post('http://localhost:8000/file/fileAllcount', {
          token: token,
        });
        setTotalFile(totalFilesResponse.data.totalFiles || 0);

        // Récupérer le nombre de fichiers pour la date sélectionnée
        const filesByDateResponse = await axios.post('http://localhost:8000/file/fileCountByDate', {
          token: token,
          date: selectedDate,
        });
        setTotalFileByDate(filesByDateResponse.data.filesCount || 0);

        // Récupérer la répartition des fichiers par utilisateur
        const filesByUsersResponse = await axios.post('http://localhost:8000/file/repartition', {
          token: token,
        });
        setFileByUser(filesByUsersResponse.data.users || []);

        // Récupérer tous les fichiers des utilisateurs
        const allFilesResponse = await axios.post('http://localhost:8000/file/showAllFiles', {
          token: token,
        });
        setAllFilesOfUsers(allFilesResponse.data.files || []);

        setLoading(false);
      } catch (err) {
        console.error('Erreur lors de la récupération des données:', err);
        setError('Une erreur est survenue lors de la récupération des données.');
        setLoading(false);
      }
    };

    fetchData();
  }, [token, selectedDate]);

  // Configuration des données du graphique
  const chartData = {
    labels: ['Total de fichiers uploadés', `Fichiers uploadés le ${formatDate(selectedDate)}`],
    datasets: [
      {
        label: 'Nombre de fichiers',
        data: [totalFile, totalFileByDate],
        backgroundColor: ['#4BC0C0', '#36A2EB'],
        borderColor: ['#4BC0C0', '#36A2EB'],
        borderWidth: 1,
      },
    ],
  };

  // Options du graphique
  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        labels: {
          color: 'black',
          font: {
            size: 14,
          },
        },
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          color: 'black',
          font: {
            size: 12,
          },
        },
      },
      x: {
        ticks: {
          color: 'black',
          font: {
            size: 12,
          },
        },
      },
    },
  };

  // Fonction pour rendre le graphique en fonction du type sélectionné
  const renderChart = () => {
    switch (chartType) {
      case 'line':
        return <Line data={chartData} options={chartOptions} />;
      case 'pie':
        return <Pie data={chartData} options={chartOptions} />;
      case 'radar':
        return <Radar data={chartData} options={chartOptions} />;
      default:
        return <Bar data={chartData} options={chartOptions} />;
    }
  };

  if (error) {
    return (
      <div className="error-container">
        <p>{error}</p>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="loading-container">
        <p>Chargement des données...</p>
      </div>
    );
  }

  return (
    <>
      {role === 'Admin' ? (
        <div className="admin-container">
          <div className="controls-container">
            <div className="control-item">
              <label htmlFor="chartType">Type de graphique :</label>
              <select
                id="chartType"
                value={chartType}
                onChange={(e) => setChartType(e.target.value)}
              >
                <option value="bar">Bar</option>
                <option value="line">Ligne</option>
                <option value="pie">Camembert</option>
                <option value="radar">Radar</option>
              </select>
            </div>
            <div className="control-item">
              <label htmlFor="datePicker">Sélectionner une date :</label>
              <input
                type="date"
                id="datePicker"
                value={selectedDate}
                onChange={(e) => setSelectedDate(e.target.value)}
                max={new Date().toISOString().split('T')[0]} // Empêche la sélection d'une date future
              />
            </div>
          </div>

          <div className="chart-container">{renderChart()}</div>

          <h2 className="title_table">Liste des utilisateurs</h2>

          <div className="infos_users">
            <table className="infosTable">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Nom et Prénom</th>
                  <th>Nombre de fichiers uploadés</th>
                  <th>Stockage utilisé</th>
                </tr>
              </thead>
              <tbody>
                {totalFileByUser.map((user) => (
                  <tr key={user.userId}>
                    <td>{user.userId}</td>
                    <td>
                      {user.nom} {user.prenom}
                    </td>
                    <td>{user.nombre_de_fichiers}</td>
                    <td>
                      {`${(user.stockageUtilise / 1073741824).toFixed(2)} Go / ${(user.quantiteStockage / 1073741824).toFixed(2)} Go`}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <h2 className="title_table">Liste de tous les fichiers</h2>

          <div className="all_files_container">
            <table className="infosTable">
              <thead>
                <tr>
                  <th>ID Fichier</th>
                  <th>Nom du fichier</th>
                  <th>Taille</th>
                  <th>Date de création</th>
                  <th>ID Utilisateur</th>
                </tr>
              </thead>
              <tbody>
                {allFilesOfUsers.map((file) => (
                  <tr key={file.id}>
                    <td>{file.id}</td>
                    <td>{file.nom}</td>
                    <td>{(file.Taille / 1048576).toFixed(2)} Mo</td>
                    <td>{new Date(file.createdAt).toLocaleDateString('fr-FR')}</td>
                    <td>{file.userid}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ) : (
        <div className="denied-container">
          <div className="overlay">
            <h1 className="denied-text">Vous n'avez pas les droits d'accès à cette page</h1>
          </div>
        </div>
      )}
    </>
  );
}

export default PageAdmin;
