import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';
import Cookies from 'js-cookie';
import { jwtDecode } from 'jwt-decode'; // Importez jwtDecode depuis 'jwt-decode'
import jsPDF from 'jspdf';

const generateTicketPDF = (eventName, eventDate, eventLocation, eventDescription, eventTime, eventPrice, numTickets, eventId) => {
    
  const userRole = sessionStorage.getItem('Role');

     
  const doc = new jsPDF();
  const formattedDate = new Date(eventDate).toLocaleDateString('fr-FR');
  const content = `


  Voici votre facture:

    nom,: ${eventName}
    prénom,: ${formattedDate} 
    Lieu: ${eventLocation}
    adresse postale: ${eventDescription}
    Nom de société: ${eventPrice} €
    Adresse Postale de la société: ${numTickets}
    siret: ${eventPrice * numTickets} €
    Date de la facture:
    désignation:
    prix unitaire hors taxe
    Montant de la TVA :
    Montant toutes taxes comprises à régler:
  `;
  doc.text(content, 10, 10);
  doc.save('billet_evenement.pdf');
}


function LoginForm() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');




  
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
  
      console.log('Sending login request...');
      const response = await axios.post('http://localhost:8000/user/login', {
        email: email,
        password: password
      });


      if (response.status === 200) {
     
        sessionStorage.setItem('Role',JSON.stringify(response.data.role));
        
        Cookies.set('test', JSON.stringify(response.data), { expires: 7 }); // Le cookie expire dans 7 jours
        const value = Cookies.get('test');
 const tokenData = JSON.parse(value);
        
        // Extraire l'e-mail du token
        const decodedToken = jwtDecode(tokenData); // Utilisez jwtDecode
        const userEmail = decodedToken.id;
        alert(userEmail);
    


      } else {
        setError('Identifiants incorrects. Veuillez réessayer.');
      }
    } catch (error) {
      setError('Une erreur s\'est produite. Veuillez réessayer plus tard.');
      if (error.response) {
        console.error('Error response data:', error.response.data);
        console.error('Error response status:', error.response.status);
        console.error('Error response headers:', error.response.headers);
        setError(error.response.data.error || 'Une erreur s\'est produite. Veuillez réessayer plus tard.');
      } else if (error.request) {
        console.error('Error request:', error.request);
        setError('Aucune réponse du serveur. Veuillez vérifier votre connexion.');
      } else {
        console.error('Error message:', error.message);
        setError('Une erreur s\'est produite. Veuillez réessayer plus tard.');
      }
      console.error('Erreur lors de la connexion :', error);
    }
  };

  return (
      <div className="login-container">
        <label className='title'>Bienvenue</label>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className='title2' htmlFor="email">Email</label>
            <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
            />
          </div>
          <div className="form-group">
            <label className='title2' htmlFor="password">Mot de passe</label>
            <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
            />
          </div>
          {error && <div className="error-message">{error}</div>}
          <button type="submit">Se connecter</button>
        </form>
      </div>
  );
}

export default LoginForm;
