import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import {jwtDecode} from 'jwt-decode'; // Corrigé l'importation de jwtDecode
import './PageUser.css';
import logo from '../img/fichier.png';
import Delete from '../img/delete.png';

function PageAccueil() {
  const [role, setRole] = useState(''); // État initial vide
  const [userId, setUserId] = useState('');
  const [token, setToken] = useState('');
  const [loaded, setLoaded] = useState(false);

  useEffect(() => {
    const value = Cookies.get('token');
    if (value) {
      const tokenData = JSON.parse(value);
      const decodedToken = jwtDecode(tokenData);
      
      setUserId(decodedToken.id);
      setToken(tokenData);
      setRole(decodedToken.role);
    }
    setLoaded(true); // Indiquer que les informations ont été chargées
  }, []);

  useEffect(() => {
    if (loaded && role === '') {
      window.location.href = '/Denied';
    }
  }, [loaded, role]);

  if (!loaded) {
    return <div>Loading...</div>;
  }

  return (
    <>
      Page admin
    </>
  );
}

export default PageAccueil;
