import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Login.css';
import Cookies from 'js-cookie';
import { jwtDecode } from 'jwt-decode'; // Importez jwtDecode depuis 'jwt-decode'

function PageAccueil() {
  const [token, setToken] = useState('');
  const [userId, setUserid] = useState('');
  const [responseStatus, setResponseStatus] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      const value = Cookies.get('token');
      const tokenData = JSON.parse(value);
      const decodedToken = jwtDecode(tokenData);
      setUserid(decodedToken.id);
      setToken(decodedToken.token);
      
      try {
        const response = await axios.post('http://localhost:8000/file/show', {
          token: decodedToken.token,
          userID: decodedToken.id
        });
        if (response.status === 200) {
          setResponseStatus(response.id);
        }
      } catch (error) {
        console.error('Une erreur s\'est produite lors de la récupération des événements :', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="login-container">
      <label className='title'>Statut de la réponse : {responseStatus}</label>
    </div>
  );
}

export default PageAccueil;
