import React, { useState } from 'react';
import axios from 'axios';
import './Inscription.css';
import jsPDF from 'jspdf';
import logo from '../img/logocard.png'; // Tell webpack this JS file uses this image

const generateTicketPDF = async (nom, prenom, adrpostale, nomSociete, adrSociete, siret, email) => {
  const doc = new jsPDF();
  const formattedDate = new Date().toLocaleDateString('fr-FR');
  const content = `
    Voici votre facture:

    Nom: ${nom}
    Prénom: ${prenom} 
    Adresse postale: ${adrpostale}
    Nom de société: ${nomSociete}
    Adresse postale de la société: ${adrSociete}
    SIRET: ${siret} 
    Date de la facture: ${formattedDate}
    Désignation:
    Quantité: 1
    Total hors taxe: 17,50 €
    Prix unitaire hors taxe:
    Montant de la TVA:
    Montant toutes taxes comprises à régler: 20€
  `;
  doc.text(content, 10, 10);

  // Convertir le PDF en blob
  const pdfBlob = doc.output('blob');

  // Créer un objet FormData pour envoyer le fichier PDF
  const formData = new FormData();
  formData.append('file', pdfBlob, `facture_${nom}_${prenom}.pdf`);

  try {
    // Envoyer le PDF via l'API addFile
    const fileResponse = await axios.post('http://localhost:8000/user/addfile', formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    });

    if (fileResponse.status === 200) {
      console.log('Fichier PDF téléchargé avec succès:', fileResponse.data);
    } else {
      console.error('Erreur lors du téléchargement du fichier:', fileResponse.data);
    }

    // Envoyer le PDF par email
    const emailResponse = await axios.post('http://localhost:8000/user/sendmail', {
      to: email,
      text: 'Voici votre facture',
      fichier: await pdfToBase64(pdfBlob), // Fonction pour convertir le blob en base64
    });

    if (emailResponse.status === 200) {
      alert('E-mail envoyé avec succès');
    } else {
      alert('Erreur lors de l\'envoi de l\'e-mail:', emailResponse);
    }

  } catch (error) {
    console.error('Erreur lors du téléchargement ou de l\'envoi:', error);
    alert('Erreur lors du téléchargement du fichier ou de l\'envoi de l\'e-mail');
  }
};

// Fonction utilitaire pour convertir un Blob en base64
const pdfToBase64 = (blob) => {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(blob);
    reader.onloadend = () => {
      const base64data = reader.result.split(',')[1]; // Extraire uniquement la chaîne base64
      resolve(base64data);
    };
    reader.onerror = (error) => {
      reject(error);
    };
  });
};




function Inscription() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [nom, setNom] = useState('');
  const [prenom, setPrenom] = useState('');
  const [adressePostal, setAdressePostal] = useState('');
  const [nomSociete, setNomSociete] = useState('');
  const [adrSociete, setAdrSociete] = useState('');
  const [siret, setSiret] = useState('');
  const [cardNumber, setCardNumber] = useState('');
  const [cardName, setCardName] = useState('');
  const [cardDate, setCardDate] = useState('');
  const [cardCode, setCardCode] = useState('');
  const [isPaymentFormVisible, setIsPaymentFormVisible] = useState(false);
  const [adminPassword, setAdminPassword] = useState('');

  const handleShowPaymentForm = () => {
    setIsPaymentFormVisible(true);
  };

  const handleSubmit = async (e) => {
    
    e.preventDefault();
    setError('');

  // Vérifier le mot de passe Admin
  const role = adminPassword === '1234' ? 'Admin' : 'User';


  //ici on appelle l'api qui permet de créer l'utilisateur avec les informations qu'il à rentrée
    try {
      console.log('Sending registration request...');
      const response = await axios.post('http://localhost:8000/user/createUser', {
        nom: nom,
        prenom: prenom,
        email: email,
        adresse_postale: adressePostal,
        adresse_postale_societe: adrSociete,
        Nom_de_societe: nomSociete,
        siret: siret,
        password: password,
        quantiteStockage: 21474836480,
        stockagedisponible: 21474836480,
        role: role,
      });

      console.log('Registration response:', response);

      if (response.status === 200) {
        alert('Inscription réussie');
        await generateTicketPDF(nom, prenom, adressePostal, nomSociete, adrSociete, siret, email);
        window.location.href = '/';
      }
    } catch (error) {
      setError('Une erreur s\'est produite. Veuillez réessayer plus tard.');
      if (error.response) {
        console.error('Error response data:', error.response.data);
        console.error('Error response status:', error.response.status);
        setError(error.response.data.error || 'Une erreur s\'est produite. Veuillez réessayer plus tard.');
      } else if (error.request) {
        console.error('Error request:', error.request);
        setError('Aucune réponse du serveur. Veuillez vérifier votre connexion.');
      } else {
        console.error('Error message:', error.message);
        setError('Une erreur s\'est produite. Veuillez réessayer plus tard.');
      }
      console.error('Erreur lors de la connexion :', error);
    }
  };


  
