import React, { useState } from 'react';
import axios from 'axios';
import './NewTarifs.css';
import Cards from 'react-credit-cards';
import 'react-credit-cards/es/styles-compiled.css';
import jsPDF from 'jspdf';
import logo from '../img/logocard.png';

function NewTarifs() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [nom, setNom] = useState('');
    const [prenom, setPrenom] = useState('');
    const [adressePostal, setAdressePostal] = useState('');
    const [nomSociete, setNomSociete] = useState('');
    const [adrSociete, setAdrSociete] = useState('');
    const [siret, setSiret] = useState('');
    const [cardNumber, setCardNumber] = useState('');
    const [cardName, setCardName] = useState('');
    const [cardDate, setCardDate] = useState('');
    const [cardCode, setCardCode] = useState('');
    const [focus, setFocus] = useState('');

    const handleInputFocus = (e) => {
        setFocus(e.target.name);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        switch (name) {
            case 'cardName':
                setCardName(value);
                break;
            case 'cardNumber':
                setCardNumber(value);
                break;
            case 'cardDate':
                setCardDate(value);
                break;
            case 'cardCode':
                setCardCode(value);
                break;
            default:
                break;
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        // Process payment logic...
    };

    return (
        <div className='moreStock'>
            <form onSubmit={handleSubmit}>
                <img className='logo' src={logo} alt="Logo" />

                <Cards
                    cvc={cardCode}
                    expiry={cardDate}
                    focused={focus}
                    name={cardName}
                    number={cardNumber}
                />

                <div className="form-group">
                    <label className='label-card'>Nom du propriétaire:</label>
                    <input
                        type="text"
                        name="cardName"
                        value={cardName}
                        onChange={handleInputChange}
                        onFocus={handleInputFocus}
                        maxLength="30"
                        placeholder="Nom sur la carte"
                        required
                    />
                </div>
                <div className="form-group">
                    <label className='label-card'>Numéro de carte bancaire:</label>
                    <input
                        type="text"
                        name="cardNumber"
                        value={cardNumber}
                        onChange={handleInputChange}
                        onFocus={handleInputFocus}
                        maxLength="19"
                        placeholder="XXXX XXXX XXXX XXXX"
                        required
                    />
                </div>
                <div className="form-group">
                    <label className='label-card'>Date d'expiration:</label>
                    <input
                        type="text"
                        name="cardDate"
                        value={cardDate}
                        onChange={handleInputChange}
                        onFocus={handleInputFocus}
                        placeholder="MM/AA"
                        required
                    />
                </div>
                <div className="form-group">
                    <label className='label-card'>Code secret:</label>
                    <input
                        type="text"
                        name="cardCode"
                        value={cardCode}
                        onChange={handleInputChange}
                        onFocus={handleInputFocus}
                        maxLength="3"
                        placeholder="CVC"
                        required
                    />
                </div>
                <button type="submit">Payer</button>
            </form>
        </div>
    );
}

export default NewTarifs;
