import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import {jwtDecode} from 'jwt-decode'; // Assurez-vous que jwt-decode est correctement installé
import './Admin.css';
import { Bar, Line, Pie, Radar } from 'react-chartjs-2';
import 'chart.js/auto';
import Video from '../img/city-night-panorama-moewalls-com.mp4';

function PageAdmin() {
  const [role, setRole] = useState('');
  const [userId, setUserId] = useState('');
  const [token, setToken] = useState('');
  const [totalFile, setTotalFile] = useState(0); // Initialisé à 0 pour commencer
  const [totalFileNow, setTotalFileNow] = useState(0);
  const [chartType, setChartType] = useState('bar');
  const [totalFileByUser, setFileByUser] = useState([]);
  const [AllFileOfUsers, setAllfilesOFUsers] = useState([]);

  // États pour le filtrage et le tri
  const [filterNom, setFilterNom] = useState('');
  const [filterDate, setFilterDate] = useState('');
  const [filterUserId, setFilterUserId] = useState('');
  const [sortType, setSortType] = useState('date');

  useEffect(() => {
    const value = Cookies.get('token');
    if (value) {
      const decodedToken = jwtDecode(value);
      setUserId(decodedToken.id);
      setRole(decodedToken.role);
      setToken(value); // Utilisation du token brut si nécessaire
    }
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [totalFilesRes, totalFilesNowRes, fileByUsersRes, allFilesOFUsersRes] = await Promise.all([
          axios.post('http://localhost:8000/file/fileAllcount', { token }),
          axios.post('http://localhost:8000/file/fileAllcountNow', { token }),
          axios.post('http://localhost:8000/file/repartition', { token }),
          axios.post('http://localhost:8000/file/showAllFiles', { token })
        ]);

        setTotalFile(totalFilesRes.data);
        setTotalFileNow(totalFilesNowRes.data);
        setFileByUser(fileByUsersRes.data);
        setAllfilesOFUsers(allFilesOFUsersRes.data);
      } catch (error) {
        console.error('Une erreur s\'est produite lors de la récupération des données :', error);
      }
    };

    fetchData();
  }, [token]);

  const data = {
    labels: ['Total de fichiers uploadés', 'Fichiers uploadés aujourd\'hui'],
    datasets: [
      {
        label: 'Nombre de fichiers',
        data: [totalFile, totalFileNow],
        backgroundColor: '#5eabbae5',
        borderColor: 'rgba(75,192,192,1)',
        borderWidth: 1,
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        labels: {
          color: 'white',
          font: {
            size: 20
          }
        }
      },
      tooltip: {
        titleColor: 'white',
        bodyColor: 'white',
        backgroundColor: '#5eabbae5',
      }
    },
  };

  const renderChart = () => {
    switch (chartType) {
      case 'line':
        return <Line data={data} options={options} />;
      case 'pie':
        return <Pie data={data} options={options} />;
      case 'radar':
        return <Radar data={data} options={options} />;
      default:
        return <Bar data={data} options={options} />;
    }
  };

  // Filtrage et tri des fichiers
  const filteredFiles = AllFileOfUsers
    .filter(file => 
      (filterNom === '' || file.nom.toLowerCase().includes(filterNom.toLowerCase())) &&
      (filterDate === '' || file.createdAt.includes(filterDate)) &&
      (filterUserId === '' || file.userid.toString().includes(filterUserId))
    )
    .sort((a, b) => {
      if (sortType === 'date') {
        return new Date(b.createdAt) - new Date(a.createdAt);
      } else if (sortType === 'size') {
        return b.Taille - a.Taille;
      }
      return 0;
    });

  return (
    <>
      <video autoPlay muted loop className="background-video">
        <source src={Video} type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      {role === 'Admin' ? (
        <div>
          <h1 className='title_table'>Choisir le type de graphique :</h1>
          <div className='select'>
            <select className='optionSelect' id="chartType" value={chartType} onChange={(e) => setChartType(e.target.value)}>
              <option value="bar">Bar</option>
              <option value="line">Line</option>
              <option value="pie">Pie</option>
              <option value="radar">Radar</option>
            </select>
          </div>
          <div className='tableStat'>
            {renderChart()}
          </div>

          <h2 className='title_table'>Liste des utilisateurs</h2>
          <div className='infos_users'>
            <table className='infosTable'>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Nom et prénom</th>
                  <th>Nombre de fichiers uploadés</th>
                  <th>Stockage restant (Go)</th>
                </tr>
              </thead>
              <tbody>
                {totalFileByUser.map(user => (
                  <tr key={user.userId}>
                    <td>{user.userId}</td>
                    <td>{user.nom} {user.prenom}</td>
                    <td>{user.nombre_de_fichiers}</td>
                    <td>{parseFloat(user.stockagedisponible / 1073741824).toFixed(3) + ' Go utilisé sur les ' + parseFloat(user.quantiteStockage / 1073741824).toFixed(3) + ' Go'}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <h2 className='title_table'>Liste des fichiers</h2>
          <div className='select2'>
            <input
              className='search-input2'
              type="text"
              placeholder="Filtrer par nom de fichier"
              value={filterNom}
              onChange={(e) => setFilterNom(e.target.value)}
            />
            <input
              className='filter-select2'
              type="date"
              placeholder="Filtrer par date"
              value={filterDate}
              onChange={(e) => setFilterDate(e.target.value)}
            />
            <input
              className='sort-select2'
              type="text"
              placeholder="Filtrer par ID d'utilisateur"
              value={filterUserId}
              onChange={(e) => setFilterUserId(e.target.value)}
            />
            <label className='sort-label'>
              Trier par :
              <select
                className='sort-select'
                value={sortType}
                onChange={(e) => setSortType(e.target.value)}
              >
                <option value="date">Date d'upload</option>
                <option value="size">Poids</option>
              </select>
            </label>
          </div>

          <table className='infosTable2'>
            <thead>
              <tr>
                <th>ID</th>
                <th>Nom du fichier</th>
                <th>Taille</th>
                <th>Date de création</th>
                <th>ID de l'utilisateur</th>
                <th>Email de l'utilisateur</th>
              </tr>
            </thead>
            <tbody className='allfiles'>
              {filteredFiles.map(file => (
                <tr key={file.id}>
                  <td>{file.id}</td>
                  <td>{file.nom}</td>
                  <td>{parseFloat(file.Taille / 1073741824).toFixed(5) + ' Go '}</td>
                  <td>{file.createdAt}</td>
                  <td>{file.userid}</td>
                  <td>{file.userEmail}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="denied-container">
          <div className="overlay">
            <h1 className="denied-text">Vous n'avez pas le droit</h1>
          </div>
        </div>
      )}
    </>
  );
}

export default PageAdmin;
