import React, { useState } from 'react';
import './App.css';
import axios from 'axios';
import Accueil from './Accueil';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

function LoginForm() {
  // Définir les états pour email, mot de passe et chargement
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);

  // Fonction de gestion de la soumission du formulaire
  const handleSubmit = (event) => {
    event.preventDefault(); // Empêcher le comportement par défaut du formulaire
    setLoading(true); // Indiquer que la requête est en cours

    axios.post('http://127.0.0.1:8000/user/login', {
      email: email,
      password: password
    })
    .then((response) => {
      console.log(response.data);
      setLoading(false); 
      history.push('/dashboard'); // Remplacez '/dashboard' par le chemin de votre composant de tableau de bord

    })
    .catch((error) => {
      console.log(error);
      setLoading(false); // Réinitialiser l'état de chargement en cas d'erreur
    });

    setEmail('');
    setPassword('');

  };

}

export default LoginForm;
