import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import { jwtDecode } from 'jwt-decode';
import './PageUser.css';
import logo from '../img/fichier.png'; // Tell webpack this JS file uses this image

function PageAccueil() {
  const [token, setToken] = useState('');
  const [userId, setUserid] = useState('');
  const [files, setFiles] = useState([]);
  const [fileBlobs, setFileBlobs] = useState([]);
  const [nomFiles, setNomFiles] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const value = Cookies.get('token');
      const tokenData = JSON.parse(value);
      const decodedToken = jwtDecode(tokenData);
      setUserid(decodedToken.id);
      setToken(decodedToken.token);
      
      try {
        const response = await axios.post('http://localhost:8000/file/show', {
          token: decodedToken.token,
          userID: decodedToken.id
        });

        // Vérifiez si la réponse est OK
        if (response.status === 200) {
          const responseData = response.data;
          const fetchedFiles = responseData.map(item => item.file);
          const fetchedNomFiles = responseData.map(item => item.nom);
          setFiles(fetchedFiles);
          setNomFiles(fetchedNomFiles);

          // Crée un blob pour chaque fichier PDF
          const fetchedFileBlobs = fetchedFiles.map(fileData => new Blob([new Uint8Array(fileData.data)], { type: 'application/pdf' }));
          setFileBlobs(fetchedFileBlobs);
        }
      } catch (error) {
        console.error('Une erreur s\'est produite lors de la récupération des fichiers :', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="upload-container">
      <label className='title'>Voici vos fichiers :</label>

      {/* Afficher les noms des fichiers */}
      {nomFiles.map((nom, index) => (
        <div key={index}>
          <h3>{nom.nom}</h3>
          {/* Afficher les fichiers associés */}
          {fileBlobs[index] && (
            <a href={URL.createObjectURL(fileBlobs[index])} download={nom}>
              <br />
              <img className='logo' src={logo} alt="Logo" />
            </a>
          )}
        </div>
      ))}
    </div>
  );
}

export default PageAccueil;
