import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import { jwtDecode } from 'jwt-decode';
import './PageUser.css';
import logo from '../img/fichier.png';
import Delete from '../img/delete.png';

function PageUser() {
  const [files, setFiles] = useState([]);
  const [userId, setUserid] = useState('');
  const [token, setToken] = useState('');
  const [denied, setDenied] = useState(false);
  const [quantiteStockage, setQuantiteStockage] = useState(0);
  const [stockageDisponible, setStockageDisponible] = useState(0);
  const [userInfos, setUserInfos] = useState({});

  const [fileName, setFileName] = useState('');
  const [fileToSend, setFileToSend] = useState(null);
  const [fileTaille, setFileTaille] = useState('');

  useEffect(() => {
    const value = Cookies.get('token');
    if (value) {
      const tokenData = JSON.parse(value);
      const decodedToken = jwtDecode(tokenData);

      setUserid(decodedToken.id);
      setQuantiteStockage(decodedToken.quantiteStockage);
      setToken(tokenData);

      fetchFiles(decodedToken.id, tokenData);
      fetchUserInfos(decodedToken.id);
    } else {
      window.location.href = '/Denied';
    }
  }, []);

  const fetchFiles = async (id, tokenData) => {
    try {
      const response = await axios.post('http://localhost:8000/file/show', {
        token: tokenData,
        userID: id,
      });

      if (response.status === 200) {
        setFiles(response.data.files);
      } else {
        setDenied(true);
      }
    } catch (error) {
      console.error('Une erreur s\'est produite lors de la récupération des fichiers :', error);
    }
  };

  const fetchUserInfos = async (id) => {
    try {
      const response = await axios.post('http://localhost:8000/user/infosuser', {
        userID: id,
      });

      if (response.status === 200) {
        setUserInfos(response.data.user);
        setStockageDisponible(response.data.user.stockagedisponible);
      } else {
        setDenied(true);
      }
    } catch (error) {
      console.error('Une erreur s\'est produite lors de la récupération des informations utilisateur :', error);
    }
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setFileName(file.name);
      setFileToSend(file);
      setFileTaille(file.size);
    }
  };





// Fonction pour convertir le fichier en chaîne hexadécimale
function fileToHex(file) {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();

    reader.onload = (event) => {
      const arrayBuffer = event.target.result; // Obtient les données binaires du fichier
      const uint8Array = new Uint8Array(arrayBuffer); // Convertit en tableau d'octets

      // Convertit le tableau d'octets en chaîne hexadécimale
      const hexString = Array.from(uint8Array).map(byte => ('00' + byte.toString(16)).slice(-2)).join('');

      resolve(hexString);
    };

    reader.onerror = (error) => {
      reject(error);
    };

    reader.readAsArrayBuffer(file); // Lit le fichier en tant que tableau d'octets
  });
}

// Utilisation de la fonction pour convertir le fichier en hexadécimal
async function convertFileToHex() {
  try {
    const hexData = await fileToHex(fileToSend);
    console.log('Données hexadécimales du fichier :', hexData);
    // Envoyez hexData à votre API ou faites ce que vous devez faire avec les données hexadécimales
  } catch (error) {
    console.error('Une erreur s\'est produite lors de la conversion du fichier en hexadécimal :', error);
  }
}



