import React, { useState,useEffect } from 'react';
import axios from 'axios';
import './NewTarifs.css';  // Assurez-vous que ce fichier contient les styles adaptés
import jsPDF from 'jspdf';
import logo from '../img/logocard.png';

import Cookies from 'js-cookie';
import {jwtDecode} from 'jwt-decode';
import { response } from 'express';
function NewTarifs() {

    const [id, setID] = useState();
    const [email, setEmail] = useState('');
    const [error, setError] = useState('');
    const [nom, setNom] = useState('');
    const [prenom, setPrenom] = useState('');
    const [adressePostal, setAdressePostal] = useState('');
    const [nomSociete, setNomSociete] = useState('');
    const [adrSociete, setAdrSociete] = useState('');
    const [siret, setSiret] = useState('');
    const [cardNumber, setCardNumber] = useState('');
    const [cardName, setCardName] = useState('');
    const [cardDate, setCardDate] = useState('');
    const [cardCode, setCardCode] = useState('');
    const [token, setToken] = useState('');
    const [stockagedisponible, setSockagedisponible] = useState('');

    useEffect(() => {
        const value = Cookies.get('token');
        if (value) {
          const tokenData = JSON.parse(value);
          const decodedToken = jwtDecode(tokenData);
          
          
          
          setID(decodedToken.id);
          setEmail(decodedToken.email);
          setNom(decodedToken.nom);
          setPrenom(decodedToken.prenom);
          setAdressePostal(decodedToken.adresse_postale);
          setNomSociete(decodedToken.nomsociete);
          setAdrSociete(decodedToken.adresse_postale_societe);
          setSiret(decodedToken.siret);
          setToken(tokenData);
    
        } else {
          window.location.href = '/Denied';
        }
      }, []);



    const generateTicketPDF = async () => {
        const doc = new jsPDF();
        const formattedDate = new Date().toLocaleDateString('fr-FR');
        const content = `
          Voici votre facture:
                    Voici votre facture pour l'achat supplémentaire de 20Go de stockage.
                    Vous avez donc 40Go de stockage !
                            
      
          Nom:                                                  ${nom}
          Prénom:                                               ${prenom} 
          Adresse postale:                                      ${adressePostal}
          Nom de société:                                       ${nomSociete}
          Adresse postale de la société:                        ${adrSociete}
          SIRET:                                                ${siret} 
          Date de la facture:                                   ${formattedDate}
          Désignation:
          Quantité: 1
          Total hors taxe: 17,50 €
          Prix unitaire hors taxe:
          Montant de la TVA:
          Montant toutes taxes comprises à régler: 20€
        `;
        doc.text(content, 10, 10);
      
        // Convertir le PDF en blob
        const pdfBlob = doc.output('blob');
      
        // Lire le contenu du blob en tant que base64
        const reader = new FileReader();
        reader.readAsDataURL(pdfBlob);
        reader.onloadend = async function () {
          try {
            const response = await axios.post('http://localhost:8000/user/senmail', {
              to: email,
              text: 'Voici votre facture',
              fichier: reader.result.split(',')[1], // Extraire le contenu base64
            });
            alert('E-mail envoyé:', response);
          } catch (error) {
            alert('Erreur lors de l\'envoi de l\'e-mail:', error);
            console.log('Erreur lors de l\'envoi de l\'e-mail:', error);
          }
          try {
            const donnée = await axios.post('http://localhost:8000/user/UserStockage', {
                userID: id,
            });


            if(donnée){
                    alert('donner capter');
                    setSockagedisponible(donnée)
            }


          } catch (error) {
            alert('Erreur lors de l\'envoi de l\'e-mail:', error);
            console.log('Erreur lors de l\'envoi de l\'e-mail:', error);
          }


          try {
            const update = await axios.post('http://localhost:8000/user/add20go', {
              email: email,
             
            });
            alert('tentative d envoie');
                  // Envoyer le fichier PDF
                  const formData = new FormData();
                  formData.append('file', pdfBlob, 'facture.pdf'); // Ajoutez un nom de fichier pour le blob
                  formData.append('nom', 'Facture du supplément de stockage');
                  formData.append('taille', pdfBlob.size); // Ajoutez la taille du fichier
                  formData.append('userid', id);
                  formData.append('stockagedisponible', 20000000000); // Assurez-vous que c'est la valeur correcte
                  formData.append('type', 'pdf');
          
                  const responseFile = await axios.post('http://localhost:8000/file/addFile', formData, {
                      headers: {
                          'Content-Type': 'multipart/form-data',
                      },
                  });


                  window.location.href = '/';
          

          } catch (error) {
            alert('Erreur', error);
          }



        
        };
      };


    
         




    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
    
        try {
            alert('Un email vous à été envoyer avec votre facture');
            await generateTicketPDF();
            
        } catch (error) {
          setError('Une erreur s\'est produite. Veuillez réessayer plus tard.');
        
        }
      };

    return (

        <>
        <h1 className='titleachat'>Achat de 20Go supplémentaire !</h1>
        <div className='moreStock'>
            
            <form onSubmit={handleSubmit} className="credit-card-form">
                <img className='logo' src={logo} alt="Logo" />

                <div className="form-group">
                    <label className='label-card'>Nom du propriétaire:</label>
                    <input
                        type="text"
                        value={cardName}
                        onChange={(e) => setCardName(e.target.value)}
                        maxLength="30"
                        placeholder="Nom sur la carte"
                        required
                        className="card-input"
                    />
                </div>

                <div className="form-group">
                    <label className='label-card'>Numéro de carte bancaire:</label>
                    <input
                        type="text"
                        value={cardNumber}
                        onChange={(e) => setCardNumber(e.target.value)}
                        maxLength="19"
                        placeholder="XXXX XXXX XXXX XXXX"
                        required
                        className="card-input"
                    />
                </div>

                <div className="form-group">
                    <label className='label-card'>Date d'expiration:</label>
                    <input
                        type="text"
                        value={cardDate}
                        onChange={(e) => setCardDate(e.target.value)}
                        placeholder="MM/AA"
                        required
                        className="card-input"
                    />
                </div>

                <div className="form-group">
                    <label className='label-card'>Code secret:</label>
                    <input
                        type="text"
                        value={cardCode}
                        onChange={(e) => setCardCode(e.target.value)}
                        maxLength="3"
                        placeholder="CVC"
                        required
                        className="card-input"
                    />
                </div>

                <button type="submit" className="pay-button">Payer</button>
            </form>
        </div>

        </>
    );
}

export default NewTarifs;
