import React, { useState, useEffect } from 'react';
import { Outlet, Link } from "react-router-dom";
import "./Header.css"
import Cookies from 'js-cookie';
import { jwtDecode } from 'jwt-decode';

const Layout = () => {

  const [adminLink, setAdminLink] = useState("/Accueil");
  const [Logout, setLogout] = useState(false);
  

  useEffect(() => {
    const fetchLogout = async () => {
      try {
        const value = Cookies.get('token');
        if (value) {
            setLogout(false) 
         
        }else{
          setLogout(true)
        }
      } catch (error) {
        console.error('Une erreur s\'est produite lors de la récupération des fichiers :', error);
      }
    }
    fetchLogout();
  }, [Logout]);
  // Définissez votre fonction test pour gérer la déconnexion
  const handleLogout = () => {

    alert('Vous êtes déconnecter');
    document.cookie = "token=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
    window.location.href = '/';


  };

  return (
    <>
      <nav className="header-nav">
        <a href="/">Accueil</a>
        <a href="/User">User</a>

        <a href="/Inscription">Inscription</a>
        <a href="/PageAdmin">Page Admin</a>
        {!Logout && (
        <a className='logout' onClick={handleLogout}>Logout</a>
      )}



        <div className="animation start-home"></div>
      </nav>
      <Outlet />
    </>
  );
};

export default Layout;
