import React, { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import "./Header.css";

const StyledMenu = styled.nav`
  display: flex;
  flex-direction: column;
  justify-content: center;
  background: #EFFFFA;
  transform: ${({ open }) => open ? 'translateX(0)' : 'translateX(-100%)'};
  height: 100vh;
  text-align: left;
  padding: 2rem;
  position: fixed; /* Menu isolé de la page */
  top: 0;
  left: 0;
  transition: transform 0.3s ease-in-out;
  z-index: 999; /* Assurer que le menu ne chevauche pas les autres éléments */
  width: 300px;
  
  a {
    font-size: 2rem;
    text-transform: uppercase;
    padding: 2rem 0;
    font-weight: bold;
    letter-spacing: 0.5rem;
    color: #0D0C1D;
    text-decoration: none;
    transition: color 0.3s linear;

    &:hover {
      color: #343078;
    }
  }
`;

const StyledBurger = styled.button`
  position: fixed; /* Garder le bouton burger hors du flux */
  top: 5%;
  left: 2rem;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  width: 2rem;
  height: 2rem;
  background: transparent;
  border: none;
  cursor: pointer;
  padding: 0;
  z-index: 1000; /* S'assure que le bouton est toujours visible */

  &:focus {
    outline: none;
  }

  div {
    width: 2rem;
    height: 0.25rem;
    background: ${({ open }) => open ? '#0D0C1D' : '#EFFFFA'};
    border-radius: 10px;
    transition: all 0.3s linear;
    position: relative;
    transform-origin: 1px;

    :first-child {
      transform: ${({ open }) => open ? 'rotate(45deg)' : 'rotate(0)'};
    }

    :nth-child(2) {
      opacity: ${({ open }) => open ? '0' : '1'};
      transform: ${({ open }) => open ? 'translateX(20px)' : 'translateX(0)'};
    }

    :nth-child(3) {
      transform: ${({ open }) => open ? 'rotate(-45deg)' : 'rotate(0)'};
    }
  }
`;

const Burger = ({ open, setOpen }) => {
  return (
    <StyledBurger open={open} onClick={() => setOpen(!open)}>
      <div />
      <div />
      <div />
    </StyledBurger>
  );
};

const Menu = ({ open }) => {
  return (
    <StyledMenu open={open}>
      <a href="/">
        <span role="img" aria-label="about us">💁🏻‍♂️</span>
        About us
      </a>
      <a href="/">
        <span role="img" aria-label="price">💸</span>
        Pricing
      </a>
      <a href="/">
        <span role="img" aria-label="contact">📩</span>
        Contact
      </a>
    </StyledMenu>
  );
};

const Layout = () => {
  const [open, setOpen] = useState(false);

  return (
    <>
      <nav className="header-nav">
        {/* Ton menu principal */}
        <a href="/">Accueil</a>
        <a href="/Inscription">Inscription</a>
        <a href="/User">User</a>
        <a href="/PageAdmin">Page Admin</a>
        <a className='logout' onClick={() => alert("Logout")}>Logout</a>

        <div className="animation start-home"></div>
      </nav>

      {/* Bouton Burger et menu */}
      <Burger open={open} setOpen={setOpen} />
      <Menu open={open} />

      {/* Outlet pour les autres pages */}
      <Outlet />
    </>
  );
};

export default Layout;
