import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import {jwtDecode} from 'jwt-decode';
import './Admin.css';
import { Bar, Line, Pie, Radar } from 'react-chartjs-2';
import 'chart.js/auto';

function PageAdmin() {
  const [role, setRole] = useState('');
  const [userId, setUserId] = useState('');
  const [token, setToken] = useState('');
  const [denied, setDenied] = useState(false);
  const [totalFile, setTotalFile] = useState(2);
  const [totalFileNow, setTotalFileNow] = useState(0);
  const [chartType, setChartType] = useState('bar');

  useEffect(() => {
    const value = Cookies.get('token');
    if (value) {
      const decodedToken = jwtDecode(value);
      const tokenData = JSON.parse(value);

      setUserId(decodedToken.id);
      setRole(decodedToken.role);
      setToken(tokenData);
    }
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post('http://localhost:8000/file/fileAllcount', {
          token: token
        });
        if (response.status === 200) {
          setTotalFile(response.data);
        }
      } catch (error) {
        console.error('Une erreur s\'est produite lors de la récupération des fichiers :', error);
      }

      try {
        const responseNow = await axios.post('http://localhost:8000/file/fileAllcountNow', {
          token: token
        });
        if (responseNow.status === 200) {
          setTotalFileNow(responseNow.data);
        }
      } catch (error) {
        console.error('Une erreur s\'est produite lors de la récupération des fichiers :', error);
      }
    };

    fetchData();
  }, [token]);

  const data = {
    labels: ['Total de fichier uplouade', 'Fichier uplouader aujourd\'hui'],
    datasets: [
      {
        label: 'Sales 2021 (M)',
        data: [totalFile, totalFileNow],
        backgroundColor: 'rgba(75,192,192,0.6)',
        borderColor: 'rgba(75,192,192,1)',
        borderWidth: 1,
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  const renderChart = () => {
    switch (chartType) {
      case 'line':
        return <Line data={data} options={options} />;
      case 'pie':
        return <Pie data={data} options={options} />;
      case 'radar':
        return <Radar data={data} options={options} />;
      default:
        return <Bar data={data} options={options} />;
    }
  };

  return (
    <>
      {role == 'User' ? (
        <div>
          <div>
            <label htmlFor="chartType">Choisir le type de graphique : </label>
            <select id="chartType" value={chartType} onChange={(e) => setChartType(e.target.value)}>
              <option value="bar">Bar</option>
              <option value="line">Line</option>
              <option value="pie">Pie</option>
              <option value="radar">Radar</option>
            </select>
          </div>
          <div style={{ height: '50vh', width: '100vw' }}>
            {renderChart()}
          </div>
        </div>
      ) : (
        <div className="denied-container">
          <div className="overlay">
            <h1 className="denied-text">Vous n'avez pas le droit</h1>
          </div>
        </div>
      )}
    </>
  );
}

export default PageAdmin;
