import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import {jwtDecode} from 'jwt-decode';
import './PageUser.css'
import logo from '../img/fichier.png'; // Tell webpack this JS file uses this image

function PageAccueil() {
  const [token, setToken] = useState('');
  const [userId, setUserid] = useState('');
  const [fileBlob, setFileBlob] = useState(null);
  const [nomFile, setNomfile] = useState([]);
  const nom = {}
  useEffect(() => {
    const fetchData = async () => {
      const value = Cookies.get('token');
      const tokenData = JSON.parse(value);
      const decodedToken = jwtDecode(tokenData);
      setUserid(decodedToken.id);
      setToken(decodedToken.token);
      
      try {
        const response = await axios.post('http://localhost:8000/file/show', {
          token: decodedToken.token,
          userID: decodedToken.id
        });

        // Vérifiez si la réponse est OK
        if (response.status === 200) {
          // Extrait le fichier PDF du champ data de la réponse
          const fileData = new Uint8Array(response.data.file.data); // Convertit les octets en Uint8Array
          // Crée un blob à partir du fichier PDF
          const fileBlob = new Blob([fileData], { type: 'application/pdf' });
          setFileBlob(fileBlob);
        
          setNomfile(response.data)
        }
      } catch (error) {
        console.error('Une erreur s\'est produite lors de la récupération du fichier :', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="uplouad-container">
      <label className='title'>Voici vos fichiers :</label>

{nomFile.map((event, index)=>{
     <h3>{event.nom}</h3>
})}
      {fileBlob && (
        <a href={URL.createObjectURL(fileBlob)} download={nomFile}><br/><img className='logo' src={logo} alt="Logo" />
        </a>
      )}
    </div>
  );
}

export default PageAccueil;
