    try {
      const response = await axios.post('http://localhost:8000/file/fileAllcount', {
        token: token
      });

      if (response.status === 200) {
        setTotalFile(response.data);
      } else {
      }
    } catch (error) {
      console.error('Une erreur s\'est produite lors de la rÃ©cupÃ©ration des fichiers :', error);
    }
  };

  const allFilecountNow = async () => {
    try {
      const response = await axios.post('http://localhost:8000/file/fileAllcountNow', {
        token: token
      });

      if (response.status === 200) {
        setTotalFile(response.data);
      } else {
      }
    } catch (error) {
      console.error('Une erreur s\'est produite lors de la rÃ©cupÃ©ration des fichiers :', error);
    }
  };


  allFilecount();  
  return (
    <>
      {role !== '' ? (
        <div>admin
          <label>number:{totalFile}</label>
        </div>
        
      ) : (
        <div className="w3-display-middle">
        <h1 className="w3-jumbo w3-animate-top w3-center"><code>Access Denied</code></h1>
        <hr className="w3-border-white w3-animate-left" style={{ margin: 'auto', width: '50%' }} />
        <h3 className="w3-center w3-animate-right">You don't have permission to view this site.</h3>
        <h3 className="w3-center w3-animate-zoom">ðŸš«ðŸš«ðŸš«ðŸš«</h3>
        <h6 className="w3-center w3-animate-zoom">error code:403 forbidden</h6>
