import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import './DeleteUser.css';

import Cookies from 'js-cookie';

const DeleteUser = () => {
    const [password, setPassword] = useState(false);
    const [email, setEmail] = useState(false);
    const [passwordTrue, setPasswordTrue] = useState(false);
    const [token, setToken] = useState('');
    const [userId, setUserid] = useState('');

    useEffect(() => {
        const fetchData = async () => {
          const value = Cookies.get('token');
          const tokenData = JSON.parse(value);
          const decodedToken = jwtDecode(tokenData);
          setEmail(decodedToken.email);      
              setPasswordTrue(decodedToken.password);
              setToken(tokenData);
              setUserid(decodedToken.id);

        };
    
        fetchData();
      }, []);
    
    const handleDeleteUser = async () => {
        if(password==passwordTrue){
            try {
          const data = {
            email: email,
            password:password
          };
    
          const response = await axios.post('http://localhost:8000/user/deleteUser', data);
    
          if (response.status === 200) {
            alert('user supprimé avec succès');
            window.location.href = '/'
            document.cookie = "token=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";

          }
        } catch (error) {
          console.error('Une erreur s\'est produite lors de la suppression ', error);
        }
        }else{
            alert('votre mot de passe est incorrect')
            

        }
        
      };
    
      const handleFileUser = async () => {
        if(password==passwordTrue){
            try {
          const data = {
            token: token,
            userid:userId
          };
    
          const response = await axios.post('http://localhost:8000/file/deleteAllfileUser', data);
    
          if (response.status === 200) {

         
            console.log('ok');

          }
        } catch (error) {
          console.error('Une erreur s\'est produite lors de la suppression ', error);
        }
        }else{
            alert('votre mot de passe est incorrect')
            

        }
        
      };
    
      return (
        <>
          <p>Pour supprimer votre compte veuiller enterer votre mot de passe</p>
          <div className="inscription-container">
            <label className="title">Suppression de compte</label>
           
              <form onSubmit={(e) => { e.preventDefault(); handleDeleteUser(); }}>
               
                <div className="form-group">
                  <label className="title2" htmlFor="password">Mot de passe</label>
                  <input
                    type="password"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </div>
                <button type="submit">Supprimer</button>
              </form>
            
          </div>
        </>
      );
    }

export default DeleteUser;
